import { useState } from "react";

const initialItems = [
  { id: 1, description: "Passports", quantity: 2, packed: false },
  { id: 2, description: "Socks", quantity: 12, packed: true },
  { id: 3, description: "Book", quantity: 1, packed: false },
];

export default function App() {
  const [Items, setItems] = useState([]);

  return (
    <div className="app">
      <Logo />
      <Form />
      <PackingList items={items} />
      <Stats />
    </div>
  );
}

function Logo() {
  return <h1> üèùÔ∏è Far Away üß≥</h1>;
}

function Form() {
  const [description, setDescription] = useState("");
  const [quantity, setQuantity] = useState(1);


  function handleAddItems(item) {
    setItems((items) => [...items, item]); //adds new items without mutating the exist array of items
  }

  function handleSubmit(e) {
    e.preventDefault(); // Avoid page reload and item submit clearance
    // console.log(e);

    if (!description) return;

    const newItem = {description, quantity, packed: false, id: Date.now()};
    console.log(newItem)

    handleAddItems(newItem);

    // Set states into initial states
    setDescription('');
    setQuantity(1);
  }

  return (
    <form className="add-form" onSubmit={handleSubmit}>
      <h3>What do you need for your üòç trip? </h3>
      <select
        value={quantity}
        onChange={(e) =>
          setQuantity(
            Number(e.target.value)
            // or +e.target.value -- add plus sign at the begin to convert to a number
          )
        }
      >
        {Array.from({ length: 20 }, (_, i) => i + 1).map((num) => (
          <option value={num} key={num}>
            {" "}
            {num}
          </option>
        ))}
      </select>
      <input
        type="text"
        placeholder="Item..."
        value={description}
        onChange={(e) => {
          // console.log(e.target.value);
          setDescription(e.target.value);
        }}
      />
      <button>Add</button>
    </form>
  );
}

function PackingList() {
  return (
    <div className="list">
      <ul>
        {initialItems.map((item) => (
          <Item item={item} key={item.id} />
        ))}
      </ul>
    </div>
  );
}

function Item({ item }) {
  return (
    <li>
      <span style={item.packed ? { textDecoration: "line-through" } : {}}>
        {item.quantity} {item.description}
      </span>
      <button> ‚ùå </button>
    </li>
  );
}

function Stats() {
  return (
    <footer className="stats">
      <em> üß≥You have X items on your list, and you already packed X(X%) </em>
    </footer>
  );
}
